mapper.select:
SELECT * FROM REVIEW_TBL
WHERE PRODUCT_NO = #{productNo}

	ORDER BY ${searchColumn} ${orderCondition}


	ORDER BY REVIEW_GRADE DESC

SELECT * 
FROM REVIEW_TBL

	MEMBER_ID = #{memberId}

		AND REVIEW_TIME >= SYSDATE - #{dayBefore}


		AND (REVIEW_TIME >= #{startDate} AND REVIEW_TIME <= #{endDate}+1 )


ORDER BY REVIEW_TIME DESC

SELECT COUNT(*) 
FROM REVIEW_TBL

	MEMBER_ID = #{memberId}

		AND REVIEW_TIME >= SYSDATE - #{dayBefore}


		AND (REVIEW_TIME >= #{startDate} AND REVIEW_TIME <= #{endDate}+1 )
